sys_script_fix.script:
var data = [];
var result = [];
var resultCounter = 0;
var gr = new GlideRecord("x_0586_fitness_fitness_details");
gr.orderBy('date');
//gr.setLimit(10);
gr.query();
while (gr.next()) {
    data.push(parseFloat(gr.getValue('calories')));
}

for (var i = 7; i < data.length; i++) {
    result.push(((data[i] + data[i - 1] + data[i - 2] + data[i - 3] + data[i - 4] + data[i - 5] + data[i - 6]) / 7).toFixed(2));
}

var gr1 = new GlideRecord("x_0586_fitness_fitness_details");
gr1.addEncodedQuery("average_calories=0^ORaverage_calories=NULL");
gr1.orderBy('date');
gr1.query();
while (gr1.next()) {
    gr1.average_calories = result[resultCounter];
    resultCounter = resultCounter + 1;
    gr1.update();
    //gs.info(gr1.average_steps)
}
