sys_script_fix.script:
var data1 = [],
    data2 = [],
    data3 = [];
var result1 = [],
    result2 = [],
    result3 = [];
var resultCounter = 0;
var gr = new GlideRecord("x_0586_fitness_fitness_details");
gr.addEncodedQuery("date>=javascript:gs.dateGenerate('2022-01-01','end')");
gr.orderBy('date');
gr.query();
while (gr.next()) {
    data1.push(parseFloat(gr.getValue('calories')));
    data2.push(parseFloat(gr.getValue('heart_points')));
    data3.push(parseFloat(gr.getValue('steps')));
}

for (var i = 6; i < data1.length; i++) {
    result1.push(((data1[i] + data1[i - 1] + data1[i - 2] + data1[i - 3] + data1[i - 4] + data1[i - 5] + data1[i - 6]) / 7).toFixed(2));
    result2.push(((data2[i] + data2[i - 1] + data2[i - 2] + data2[i - 3] + data2[i - 4] + data2[i - 5] + data2[i - 6]) / 7).toFixed(2));
    result3.push(((data3[i] + data3[i - 1] + data3[i - 2] + data3[i - 3] + data3[i - 4] + data3[i - 5] + data3[i - 6]) / 7).toFixed(2));
}

var gr1 = new GlideRecord("x_0586_fitness_fitness_details");
gr1.addEncodedQuery("date>=javascript:gs.dateGenerate('2022-01-07','end')");
gr1.orderBy('date');
gr1.query();
while (gr1.next()) {
    gr1.average_calories = result1[resultCounter];
    gr1.average_heart_points = result2[resultCounter];
    gr1.average_steps = result3[resultCounter];
    resultCounter = resultCounter + 1;
    gr1.update();
}
